    let cartItems = []; 
    // Render products 
    function renderProducts(products) { 
        productList.innerHTML = ''; 
        products.forEach(product => { 
            const productDiv = document.createElement('div'); 
            productDiv.className = 'product'; productDiv.innerHTML = ` 
            <h4>${product.name}</h4> 
            <img src=${product.image} alt=${product.name}> 
            <p>Price: Rs.${product.price}</p> 
            <p>Category: ${product.category}</p> 
            <button class="add-to-cart" id="cart-${product.id}">Add to Cart</button> 
            `; 
            productList.appendChild(productDiv); 
        }); 

        // Add event listeners to "Add to Cart" buttons 
            document.querySelectorAll('.add-to-cart').forEach(button => { 
                button.addEventListener('click', () => { 
                    const productId = button.id.replace('cart-', ''); 
                    const product = products.find(p => p.id == productId); 
                    alert("Product added to cart");
                    addToCart(product); 
                }); 
            }); 
    }

    // Add to cart 
    function addToCart(product) { 
        cartItems.push(product);
        sessionStorage.setItem("cartItem",JSON.stringify(cartItems));
    } 
    

    
        
        
    
    
    renderProducts(products);

    //Search button functionality 
    const searchInput = document.getElementById('search-input'); 
    const categorySelect = document.getElementById('category-select'); 
    const searchButton = document.getElementById('search-button');
    searchButton.addEventListener('click', () => { 
        const query = searchInput.value.toLowerCase(); 
        const selectedCategory = categorySelect.value; 
        const filteredProducts = products.filter(product => { 
            const matchesName = product.name.toLowerCase().includes(query); //chaeaking product name
            const matchesCategory = selectedCategory ? product.category === selectedCategory : true; // cheaking category
            return matchesName && matchesCategory; 
        }); 
        renderProducts(filteredProducts); 
        console.log("calling search");
    });




